from langflow.custom import Component
from langflow.inputs import MessageTextInput, SecretStrInput, IntInput, BoolInput, FileInput
from langflow.template import Output
from langflow.schema import Data
import paramiko
import socket
import os
import re

class K8sGPTExecutor(Component):
    display_name = "Scanning and Report Generator"
    description = "Execute user-provided command or run all tools and save output on VM"
    icon = "terminal"
    documentation = "https://docs.langflow.org/components-custom-components"

    inputs = [
        MessageTextInput(name="host", display_name="VM Host IP", required=True),
        MessageTextInput(name="username", display_name="Username", required=True),
        SecretStrInput(name="password", display_name="Password (optional if using PEM)", required=False),
        FileInput(name="pem_file", display_name="PEM File", file_types=["pem"], required=False),
        MessageTextInput(name="command", display_name="Command to Execute", value="run all tools", required=True),
        MessageTextInput(name="folder_name", display_name="Output Folder Name (VM)", value="tool_outputs", required=True),
        IntInput(name="port", display_name="SSH Port", value=22),
        IntInput(name="timeout", display_name="Command Timeout (seconds)", value=60),
        BoolInput(name="format_output", display_name="Format Output", value=True),
    ]

    outputs = [
        Output(name="output", display_name="Command Output", method="build_output")
    ]

    def build_output(self) -> Data:
        try:
            # Extract PEM file path if it's a Langflow object
            pem_path = self.pem_file.path if hasattr(self.pem_file, 'path') else self.pem_file

            if self.command.strip().lower() == "run all tools":
                text_output = self.run_all_tools(
                    self.host, self.username, self.password, pem_path, self.port, self.folder_name
                )
                return Data(text=text_output)
            else:
                text_output = self.ssh_execute_command(
                    self.host, self.username, self.password, pem_path, self.command, self.port, self.timeout
                )
                if self.format_output:
                    text_output = self.format_output_string(text_output)
                return Data(text=text_output)
        except Exception as e:
            return Data(text=f"[ERROR] {str(e)}")

    def format_output_string(self, output: str) -> str:
        return re.sub(r'\x1b\[[0-9;]*[a-zA-Z]', '', output).strip()

    def test_ssh_connectivity(self, ip, port=22) -> bool:
        try:
            sock = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
            sock.settimeout(5)
            result = sock.connect_ex((ip, port))
            return result == 0
        finally:
            sock.close()

    def connect_with_pem(self, ip, username, pem_file_path, port=22):
        if not self.test_ssh_connectivity(ip, port):
            raise Exception("SSH port is not accessible.")

        if not pem_file_path:
            raise ValueError("PEM file path is empty or None.")

        # Handle both file path and file object
        if hasattr(pem_file_path, "read"):
            key = paramiko.RSAKey.from_private_key(pem_file_path)
        else:
            if not os.path.exists(pem_file_path):
                raise FileNotFoundError(f"PEM file not found: {pem_file_path}")
            with open(pem_file_path, "r") as file:
                key = paramiko.RSAKey.from_private_key(file)

        client = paramiko.SSHClient()
        client.set_missing_host_key_policy(paramiko.AutoAddPolicy())
        client.connect(hostname=ip, username=username, pkey=key, port=port)

        return client

    def ssh_execute_command(self, ip, username, password, pem_file_path, command, port=22, timeout=60):
        if pem_file_path:
            client = self.connect_with_pem(ip, username, pem_file_path, port)
        else:
            if not self.test_ssh_connectivity(ip, port):
                raise Exception("SSH port is not accessible.")
            client = paramiko.SSHClient()
            client.set_missing_host_key_policy(paramiko.AutoAddPolicy())
            client.connect(hostname=ip, username=username, password=password, port=port, timeout=30)

        stdin, stdout, stderr = client.exec_command(command)
        stdout.channel.settimeout(timeout)
        output = stdout.read().decode("utf-8", errors="replace") + stderr.read().decode("utf-8", errors="replace")
        client.close()
        return output.strip()

    def check_directory_exists(self, client, dir_path):
        """Check if a directory exists on the remote system."""
        stdin, stdout, stderr = client.exec_command(f"test -d {dir_path} && echo 'exists' || echo 'does not exist'")
        return stdout.read().decode('utf-8').strip()

    def ensure_directory_exists(self, client, dir_path):
        """Ensure a directory exists on the remote system, create it if it does not."""
        if self.check_directory_exists(client, dir_path) == "does not exist":
            client.exec_command(f"mkdir -p {dir_path}")

    def run_all_tools(self, ip, username, password, pem_file_path, port, folder_name):
        tools_and_commands = {
            "Bandit": f"PATH=$PATH:/home/ubuntu/.local/bin:/usr/local/bin && /home/ubuntu/.local/bin/bandit -r /home/{username}/testing -f json -o /home/{username}/{folder_name}/Bandit.txt",
            "Pylint": f"PATH=$PATH:/home/ubuntu/.local/bin:/usr/local/bin && /home/ubuntu/.local/bin/pylint /home/{username}/testing > /home/{username}/{folder_name}/Pylint.txt",
            "Flake8": f"PATH=$PATH:/home/ubuntu/.local/bin:/usr/local/bin && /home/ubuntu/.local/bin/flake8 /home/{username}/testing --format=default > /home/{username}/{folder_name}/Flake8.txt",
            "K8sGPT": f"PATH=$PATH:/home/ubuntu/.local/bin:/usr/local/bin && /usr/local/bin/k8sgpt analyze --explain > /home/{username}/{folder_name}/K8sGPT.txt",
            "Snyk": f"PATH=$PATH:/home/ubuntu/.local/bin:/usr/local/bin && /usr/local/bin/snyk code test --json > /home/{username}/{folder_name}/Snyk.txt"
        }

        if pem_file_path:
            client = self.connect_with_pem(ip, username, pem_file_path, port)
        else:
            if not self.test_ssh_connectivity(ip, port):
                raise Exception("SSH port is not accessible.")
            client = paramiko.SSHClient()
            client.set_missing_host_key_policy(paramiko.AutoAddPolicy())
            client.connect(hostname=ip, username=username, password=password, port=port)

        sftp = client.open_sftp()
        folder_path = f"/home/{username}/{folder_name}"
        self.ensure_directory_exists(client, folder_path)

        output_summary = []

        for tool, cmd in tools_and_commands.items():
            stdin, stdout, stderr = client.exec_command(cmd)
            stdout.channel.recv_exit_status()  # Don't use for success check

            # Extract filename from command (you already know the structure)
            filename = f"{folder_path}/{tool}.txt" if tool != "Snyk" else f"{folder_path}/Snyk.txt"
            try:
                sftp.stat(filename)  # Check if file was actually created
                output_summary.append(f"[SUCCESS] {tool} output saved to {filename}")
            except FileNotFoundError:
                tool_stderr = stderr.read().decode("utf-8", errors="replace")
                output_summary.append(f"[ERROR] {tool} failed. Stderr: {tool_stderr.strip()}")


        sftp.close()
        client.close()

        return "\n".join(output_summary) + "\n\n[INFO] Tool outputs saved to VM."

from langflow.custom import Component
from langflow.inputs import MessageTextInput, SecretStrInput, IntInput, FileInput
from langflow.template import Output
from langflow.schema import Data
import paramiko
import socket
import os

class SecurityReportChart(Component):
    display_name = "Security Report Chart Generator"
    description = "Generate charts on the VM from saved tool output reports"
    icon = "chart-bar"
    documentation = "https://docs.langflow.org/components-custom-components"

    inputs = [
        MessageTextInput(name="host", display_name="VM Host IP", required=True),
        MessageTextInput(name="username", display_name="Username", required=True),
        SecretStrInput(name="password", display_name="Password (optional if using PEM)", required=False),
        FileInput(name="pem_file", display_name="PEM File", file_types=["pem"], required=False),
        MessageTextInput(name="folder_name", display_name="Report Folder (on VM)", value="tool_outputs", required=True),
        IntInput(name="port", display_name="SSH Port", value=22),
        MessageTextInput(name="command", display_name="Command to Execute", value="run chart", required=True),
    ]

    outputs = [
        Output(name="output", display_name="Chart Status", method="build_output")
    ]

    def build_output(self) -> Data:
        try:
            if self.command.strip().lower() != "run chart":
                return Data(text="[INFO] Skipping chart generation (command did not match 'run chart')")

            pem_path = self.pem_file.path if hasattr(self.pem_file, 'path') else self.pem_file
            result = self.generate_charts_on_vm(
                self.host, self.username, self.password, pem_path, self.port, self.folder_name
            )
            return Data(text=result)
        except Exception as e:
            return Data(text=f"[ERROR] {str(e)}")

    def test_ssh_connectivity(self, ip, port=22) -> bool:
        sock = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        sock.settimeout(5)
        result = sock.connect_ex((ip, port))
        sock.close()
        return result == 0

    def connect(self, ip, username, password, pem_file_path, port=22):
        if not self.test_ssh_connectivity(ip, port):
            raise Exception("SSH port is not accessible.")

        client = paramiko.SSHClient()
        client.set_missing_host_key_policy(paramiko.AutoAddPolicy())

        if pem_file_path:
            key = paramiko.RSAKey.from_private_key_file(pem_file_path)
            client.connect(hostname=ip, username=username, pkey=key, port=port)
        else:
            client.connect(hostname=ip, username=username, password=password, port=port)
        return client

    def generate_charts_on_vm(self, ip, username, password, pem_file_path, port, folder_name):
        client = self.connect(ip, username, password, pem_file_path, port)
        sftp = client.open_sftp()

        folder_path = f"/home/{username}/{folder_name}"
        remote_script_path = f"/home/{username}/generate_chart.py"

        chart_script = f'''#!/usr/bin/env python3
import os
import json
import matplotlib.pyplot as plt
from collections import Counter
import re

def analyze_bandit(file_path):
    low = med = high = 0
    total = 0
    with open(file_path) as f:
        for line in f:
            if "issue_severity" in line.lower():
                line = line.lower()
                if "low" in line:
                    low += 1
                    total += 1
                elif "medium" in line:
                    med += 1
                    total += 1
                elif "high" in line:
                    high += 1
                    total += 1
    return total, {{"Low": low, "Medium": med, "High": high}}


def analyze_pylint(file_path):
    issues = {{"Convention": 0, "Refactor": 0, "Warning": 0, "Error": 0, "Fatal": 0}}
    total = 0
    pattern = re.compile(r"^([CRWEF]):")
    with open(file_path) as f:
        for line in f:
            match = pattern.match(line.strip())
            if match:
                code = match.group(1)
                if code == "C":
                    issues["Convention"] += 1
                elif code == "R":
                    issues["Refactor"] += 1
                elif code == "W":
                    issues["Warning"] += 1
                elif code == "E":
                    issues["Error"] += 1
                elif code == "F":
                    issues["Fatal"] += 1
                total += 1
    return total, issues



def analyze_flake8(file_path):
    codes = Counter()
    total = 0
    with open(file_path) as f:
        for line in f:
            if line.strip():
                parts = line.strip().split()
                if len(parts) > 1:
                    match = re.search(r'([A-Z]?[0-9]+)', parts[1])
                    if match:
                        code = match.group(0)
                        codes[code[0]] += 1
                        total += 1
    return total, dict(codes)

def plot_chart(tool_name, total, data, folder_path):
    if total == 0:
        print(f"No data for {{tool_name}}, skipping chart.")
        return

    labels = list(data.keys())
    values = list(data.values())

    plt.figure(figsize=(6, 4))
    bars = plt.bar(labels, values, color='skyblue')
    plt.title(f"{{tool_name}} - {{total}} Issues")
    plt.ylabel("Count")

    for bar, count in zip(bars, values):
        yval = bar.get_height()
        plt.text(bar.get_x() + bar.get_width()/2, yval + 0.1, str(count), ha='center', va='bottom')

    chart_name = f"{{tool_name.lower()}}_chart.png"
    plt.tight_layout()
    plt.savefig(os.path.join(folder_path, chart_name))
    plt.close()

tool_files = {{
    "Bandit": analyze_bandit,
    "Pylint": analyze_pylint,
    "Flake8": analyze_flake8
}}

for tool, analyzer in tool_files.items():
    path = os.path.join("{folder_path}", f"{{tool}}.txt")
    if os.path.exists(path):
        total, data = analyzer(path)
        plot_chart(tool, total, data, "{folder_path}")
'''


        try:
            # Upload script
            with sftp.open(remote_script_path, 'w') as f:
                f.write(chart_script)
            sftp.chmod(remote_script_path, 0o755)

            # Run script
            stdin, stdout, stderr = client.exec_command(f"python3 {remote_script_path}")
            error = stderr.read().decode("utf-8").strip()
            output = stdout.read().decode("utf-8").strip()

            if error:
                return f"[ERROR] Chart generation failed: {error}"
            return f"[SUCCESS] Charts saved in {folder_path} as bandit_chart.png, pylint_chart.png, flake8_chart.png"

        finally:
            sftp.close()
            client.close()
